public class Employee{
	int empid;
	String empname;

	Employee(int id, String name){
	this.empid = id;
	this.empname = name;
	
	}
	void main(){
	System.out.println("id: "+empid+"name: "+empname);
	}
	
	public static void main(String args[]){
	
	Employee e1 = new Employee(161,"eqfwe");
	Employee e2 = new Employee(112,"rreqr");
	e1.main();
	e2.main();
	
	}
	
	}
